"use strict";const api_key="9341597124d912b5669e6a98711aab2b";export const fetchData=function(e,t){fetch(`${e}&appid=${api_key}`).then((e=>e.json())).then((e=>t(e)))};export const url={currentWeather:(e,t)=>`http://api.openweathermap.org/data/2.5/weather?${e}&${t}&units=metric&lang=ru`,forecast:(e,t)=>`http://api.openweathermap.org/data/2.5/forecast?${e}&${t}&units=metric&lang=ru`,airPollution:(e,t)=>`http://api.openweathermap.org/data/2.5/air_pollution?${e}&${t}`,reverseGeo:(e,t)=>`http://api.openweathermap.org/geo/1.0/reverse?${e}&${t}&limit=5`,geo:e=>`http://api.openweathermap.org/geo/1.0/direct?q=${e}&limit=5`};